import Vue from "vue";
import VueRouter from "vue-router";
import Home from "../views/Home.vue"
import Use from "../views/use.vue"
Vue.use(VueRouter)

//1.创建路由组件
//2.将路由和组件做一个映射
//3.创建router实例

const routes=[
    {path:'/Home',component: Home},
    {path:'/Use',component: Use},

]


const router = new VueRouter({
    routes
})

export default router
